// Collect likely fields where the model might put the JSON
const candidates = [
  $json.schema,
  $json.output,               // <-- your case
  $json.kwargs?.content,
  $json.content,
  $json.text,
  $json.message,
  $json.response,
];

// Pick the first non-null/undefined candidate
let raw = candidates.find(v => v !== undefined && v !== null) ?? '';

function safeParse(input) {
  if (input && typeof input === 'object') return input; // already an object
  const s = String(input);
  // If the model wrapped the JSON with any extra text, extract the first {...} block
  const start = s.indexOf('{');
  const end   = s.lastIndexOf('}');
  const core  = (start >= 0 && end >= start) ? s.slice(start, end + 1) : s;

  try {
    return JSON.parse(core);
  } catch {
    // Fallback schema (so Apps Script still creates a form)
    return {
      title: "Untitled Event",
      description: "Auto-generated placeholder (invalid JSON).",
      questions: [
        { type: "shortAnswer",    title: "Full Name" },
        { type: "multipleChoice", title: "Can you attend?", options: ["Yes","No"] },
        { type: "paragraph",      title: "Dietary restrictions or comments" }
      ]
    };
  }
}

// n8n expects an array of items
return [{ json: safeParse(raw) }];

// Make a clean payload the LLM can consume
const rows = items.map(i => i.json);

// Define the metadata that will be added to each row
const eventTitle = "MSBA Mixer";
const seed = Date.now() % 1000; // deterministic tie-breaker
const columnHints = {
  attendance: ['Can you attend?', 'RSVP', 'Attending'],
  venue: ['Preferred location', 'Venue', 'Location', 'Where'],
  date: ['Preferred time slot', 'Date', 'When']
};

// Use .map() to create a new array. For each row, copy its original
// data and add the metadata to it.
const enrichedRows = rows.map(row => {
  return {
    ...row, // Copies the original row data (Full Name, Email, etc.)
    eventTitle: eventTitle,
    seed: seed,
    columnHints: columnHints
  };
});

// Return the new array of enriched rows directly
return enrichedRows;

// The data comes from the Aggregate node
var rows = $json.data;

// --- PART 1: Tally Logic ---
var tally = { venues: {}, dates: {} };
for (var i = 0; i < rows.length; i++) {
    var row = rows[i];
    var venue = row['Preferred location 📍'] || row['Preferred location'];
    var date = row['Preferred time slot ⏰'] || row['Preferred time slot'];
    if (venue) { tally.venues[venue] = (tally.venues[venue] || 0) + 1; }
    if (date) { tally.dates[date] = (tally.dates[date] || 0) + 1; }
}
var findWinner = function(tallyObject) {
    var keys = Object.keys(tallyObject);
    if (keys.length === 0) { return { winner: null, tieBroken: false }; }
    var maxVotes = 0;
    for (var key in tallyObject) { if (tallyObject[key] > maxVotes) { maxVotes = tallyObject[key]; } }
    var winners = [];
    for (var key in tallyObject) { if (tallyObject[key] === maxVotes) { winners.push(key); } }
    var tieBroken = winners.length > 1;
    var winner = winners[Math.floor(Math.random() * winners.length)];
    return { winner: winner, tieBroken: tieBroken };
};
var venueResult = findWinner(tally.venues);
var dateResult = findWinner(tally.dates);
var winningVenue = venueResult.winner;
var winningDate = dateResult.winner;
var reason = "Determined by selecting the highest-voted option.";
if (venueResult.tieBroken || dateResult.tieBroken) {
    reason = "Determined by selecting the highest-voted option. A tie was broken randomly.";
}
var eventTitle = (rows[0] && rows[0].eventTitle) || "Event RSVP";

// --- PART 2: Formatting Logic ---
var dateLines = Object.keys(tally.dates).map(function(d) { return '• ' + d + ': ' + tally.dates[d] + ' votes'; }).join('\n');
var venueLines = Object.keys(tally.venues).map(function(v) { return '• ' + v + ': ' + tally.venues[v] + ' votes'; }).join('\n');
var formattedMessage = "📊 Here are the results for **" + eventTitle + "**:\n\n" +
              "**Date Tallies:**\n" +
              (dateLines || 'No votes for dates.') + "\n\n" +
              "**Venue Tallies:**\n" +
              (venueLines || 'No votes for venues.') + "\n\n" +
              "---\n\n" +
              "🏆 **Recommendation:**\n" +
              "The winning option is **" + winningDate + "** at **" + winningVenue + "**.\n\n" +
              "*Reason: " + reason + "*\n\n" +
              "Shall I go ahead and send the calendar invitations?";

// --- PART 3: Return an object containing the formatted message ---
// --- PART 3: Return an object with all the necessary data ---
return {
  eventTitle: eventTitle,
  winners: {
    venue: winningVenue,
    date: winningDate
  },
  reason: reason,
  // We can also include the formatted message for the chat reply later
  message: formattedMessage
};

