if (!dateStr) {
  throw new Error("No date found in tallyData.winners.date");
}

// Clean up date string for JS parser
dateStr = dateStr.replace(/(\d+)(st|nd|rd|th)/, "$1"); // remove 6th -> 6

var fullDateStr = dateStr + ", 2025"; // add year

// Attempt to parse
var startDate = new Date(fullDateStr);

// If parsing failed, provide a fallback
if (isNaN(startDate.getTime())) {
  console.log("⚠️ Could not parse dateStr, using default fallback.");
  startDate = new Date(); // fallback to current time
  startDate.setHours(19, 0, 0, 0); // 7pm today
}

// Add 2 hours for event end
var endDate = new Date(startDate.getTime());
endDate.setHours(endDate.getHours() + 2);

// Return result
return {
  start: startDate.toISOString(),
  end: endDate.toISOString()
};

