function safeParse(input) {
  if (input && typeof input === 'object') return input;
  const s = String(input ?? '');
  const a = s.indexOf('{');
  const b = s.lastIndexOf('}');
  const core = (a >= 0 && b >= a) ? s.slice(a, b + 1) : s;
  try { return JSON.parse(core); } catch { return {}; }
}

const items = $input.all().map(({ json }) => {
  const raw = json.output ?? json.text ?? json;
  const out = safeParse(raw);
  return {
    json: {
      is_event_request: Boolean(out.is_event_request),
      event_idea: String(out.event_idea ?? ''),
      event_name: String(out.event_name ?? ''),
      has_time_options: Boolean(out.has_time_options),
      time_options: Array.isArray(out.time_options) ? out.time_options : [],
      agent_response: String(out.agent_response ?? '')
    }
  };
});

return items;

// --- Parse and Structure Venue Agent Output ---

let raw = $json.output || $json;
let out;

// 1. Parse safely
// This is more robust and will try to find JSON even if the LLM
// adds extra text like "Here is the JSON: ```json ... ```"
try {
  const s = String(raw);
  const start = s.indexOf('{');
  const end = s.lastIndexOf('}');

  if (start !== -1 && end !== -1 && end > start) {
    raw = s.substring(start, end + 1);
  }

  out = JSON.parse(raw);
} catch (err) {
  // If parsing fails, it's likely a plain text error from the agent
  out = {
    agent_response: String(raw),
    user_satisfied_with_venue: false, // Default to not satisfied on error
    confidence: 0.0,
    citations: [],
    venue_options: [] // <-- ADDED DEFAULT
  };
}

// 2. Extract key fields from the structured JSON
const agentResponse = out?.agent_response || "Sorry, I encountered an error processing your request.";
const isSatisfied = out?.user_satisfied_with_venue || false; // Default to false if missing
const venueOptions = out?.venue_options || []; // <-- ADDED THIS LINE
const confidence = typeof out?.confidence === "number" ? out.confidence : 0.0;

// 3. Format citations into a clean string
const sources = (out?.citations || [])
  .map(c => c.label || c.type || '') // Get the label or type
  .filter(Boolean) // Remove any empty/null labels
  .join(", ");

// 4. Return the structured data for the next steps in your workflow
// We don't need the old "isLow" logic because your new prompt
// already builds the full follow-up question into the "agent_response".
return [
  {
    json: {
      agent_response: agentResponse.trim(), // The text to show the user
      user_satisfied_with_venue: isSatisfied, // For your IF node
      venue_options: venueOptions, // <-- ADDED THIS FIELD
      confidence: confidence,
      sources: sources // The formatted citation string
    }
  }
];

// --- Parse and Structure Time Agent Output ---

let raw = $json.output ?? $json;
let out;

// 1) Safe, loose JSON extraction (handles code fences or extra prose)
function looseParse(input) {
  if (input && typeof input === 'object') return input;
  const s = String(input ?? '');
  const start = s.indexOf('{');
  const end = s.lastIndexOf('}');
  const core = (start !== -1 && end !== -1 && end > start) ? s.slice(start, end + 1) : s;
  try { return JSON.parse(core); } catch { return null; }
}

out = looseParse(raw);

// 2) Defaults if parsing failed
if (!out || typeof out !== 'object') {
  out = {
    time_confirmed: false,
    time_options: [],
    agent_response: String(raw || '').trim() || "Sorry, I couldn't parse a time from that message.",
    memory_writes: {},
    confidence: 0.0,
    debug: { low_confidence_reasons: ["parse_error"] }
  };
}

// 3) Normalize fields & types
const timeConfirmed = Boolean(out.time_confirmed);

// Ensure time_options is an array of non-empty strings
let timeOptions = [];
if (Array.isArray(out.time_options)) {
  timeOptions = out.time_options
    .filter(v => typeof v === 'string')
    .map(s => s.trim())
    .filter(Boolean);
} else if (typeof out.time_options === 'string' && out.time_options.trim()) {
  timeOptions = [out.time_options.trim()];
}

const agentResponse =
  (typeof out.agent_response === 'string' && out.agent_response.trim())
    ? out.agent_response.trim()
    : (timeConfirmed
        ? "Time confirmed."
        : "Please share 2–4 possible time windows (e.g., Fri 7–9 PM; Sat 3–5 PM).");

// memory_writes object + convenient final_time
const memoryWrites = (out.memory_writes && typeof out.memory_writes === 'object') ? out.memory_writes : {};
const finalTime = typeof memoryWrites.final_time === 'string'
  ? memoryWrites.final_time.trim()
  : (timeConfirmed && timeOptions[0] ? timeOptions[0] : null);

// Confidence & debug
const confidence = (typeof out.confidence === 'number') ? out.confidence : 0.0;
const lowConfidenceReasons = Array.isArray(out?.debug?.low_confidence_reasons)
  ? out.debug.low_confidence_reasons
  : [];

// Helpful status flags for downstream nodes
const status = timeConfirmed
  ? "time_confirmed"
  : (timeOptions.length ? "time_options_proposed" : "need_time_options");

const missingTimeOptions = timeOptions.length === 0;

// 4) Pass-through helpful context if present
const passthrough = {
  event_id: $json.event_id ?? null,
  username: $json.username ?? null,
  timezone: memoryWrites?.timezone ?? null
};

// 5) Return structured payload
return [
  {
    json: {
      time_confirmed: timeConfirmed,
      time_options: timeOptions,               // array (never null)
      agent_response: agentResponse,          // string
      final_time: finalTime,                  // string | null
      memory_writes: memoryWrites,            // object
      confidence: confidence,                 // number
      debug_low_confidence_reasons: lowConfidenceReasons, // array
      status: status,                         // "time_confirmed" | "time_options_proposed" | "need_time_options"
      missing_time_options: missingTimeOptions, // boolean (handy for IF node)
      ...passthrough
    }
  }
];

// --- Parse & Structure Questionnaire Agent Output ---
// Goal: Emit predictable fields for downstream nodes (Apps Script, DB updates, etc.)

// 1) Gather likely fields where the model might have put the JSON
const candidates = [
  $json.schema,
  $json.output,               // common
  $json.kwargs?.content,
  $json.content,
  $json.text,
  $json.message,
  $json.response,
];

// Pick first non-null
let raw = candidates.find(v => v !== undefined && v !== null) ?? '';

function looseParse(input) {
  if (input && typeof input === 'object') return input; // already JSON
  const s = String(input ?? '');
  const a = s.indexOf('{');
  const b = s.lastIndexOf('}');
  const core = (a >= 0 && b >= a) ? s.slice(a, b + 1) : s;
  try { return JSON.parse(core); } catch { return null; }
}

// 2) Parse
let out = looseParse(raw);

// 3) Helpers
const isFormObject = (obj) =>
  obj && typeof obj === 'object' && obj.title && Array.isArray(obj.questions);

const toVenueLabels = (venues) => {
  if (!Array.isArray(venues)) return [];
  return venues.map(v => {
    if (typeof v === 'string') return v;
    if (v && typeof v === 'object') {
      const name = v.venue_name || v.name || 'Unnamed';
      const addr = v.short_address || v.address || '';
      return addr ? `${name} (${addr})` : name;
    }
    return '';
  }).filter(Boolean);
};

const coerceStringArray = (arr) =>
  Array.isArray(arr) ? arr.map(x => String(x)).filter(Boolean) : [];

// Build a minimal fallback form if the agent said "true" but didn't include one
function buildFallbackForm() {
  const eventName = $json.event_name || 'Event';
  const venueOpts = toVenueLabels($json.venue_options || []).slice(0, 8);
  const timeOpts  = coerceStringArray($json.time_options || []).slice(0, 6);

  // If no structured options, give light examples to avoid an empty MCQ
  const timeChoices = timeOpts.length ? timeOpts : ["Fri 6–8 PM", "Sat 3–5 PM"];
  const venueChoices = venueOpts.length ? venueOpts : undefined; // if none, we’ll use shortAnswer fallback

  const questions = [
    { type: "shortAnswer",    title: "Full Name" },
    { type: "shortAnswer",    title: "Email" },
    { type: "multipleChoice", title: "Can you attend?", options: ["Yes","No"] },
    venueChoices
      ? { type: "multipleChoice", title: "Preferred location", options: venueChoices }
      : { type: "shortAnswer",    title: "Preferred location" },
    { type: "multipleChoice", title: "Preferred time slot", options: timeChoices },
    { type: "checkbox",       title: "Activity preference", options: ["Networking","Games","Food","Drinks"] },
    { type: "paragraph",      title: "Dietary notes or comments" },
  ];

  return {
    title: `RSVP — ${eventName}`,
    description: `Please RSVP for ${eventName}.`,
    questions
  };
}

// 4) Normalize top-level fields
let questionnaireNeeded = false;
let agentResponse = '';
let form = null;
let confidence = 0.0;
let debugReasons = [];

// Case A: correct wrapper schema
if (out && typeof out === 'object' && ('questionnaire_needed' in out || 'form' in out)) {
  questionnaireNeeded = Boolean(out.questionnaire_needed);
  agentResponse = (typeof out.agent_response === 'string' ? out.agent_response.trim() : '');
  confidence = (typeof out.confidence === 'number' ? out.confidence : 0.0);
  debugReasons = Array.isArray(out?.debug?.low_confidence_reasons) ? out.debug.low_confidence_reasons : [];

  // Only keep a form when needed
  if (questionnaireNeeded) {
    form = isFormObject(out.form) ? out.form : null;
    if (!form) {
      // salvage: build a minimal form from local context
      form = buildFallbackForm();
      debugReasons = [...debugReasons, 'missing_form_fallback'];
    }
  } else {
    form = null;
  }

// Case B: legacy/bare form schema — treat as "needed"
} else if (isFormObject(out)) {
  questionnaireNeeded = true;
  agentResponse = "Questionnaire sent. Let me know when you want me to collect the results.";
  form = out;
  confidence = 0.7;
  debugReasons = ['legacy_form_schema'];

// Case C: parse failure — be safe and skip
} else {
  questionnaireNeeded = false;
  agentResponse = "Great! The event brainstorming is done. You can start planning a new event whenever you like.";
  form = null;
  confidence = 0.0;
  debugReasons = ['parse_error'];
}

// 5) Optional sanitization of form.questions (keep only valid shapes)
if (form && isFormObject(form)) {
  const allowedTypes = new Set(["shortAnswer","multipleChoice","checkbox","paragraph","date","time"]);
  form.questions = (Array.isArray(form.questions) ? form.questions : []).filter(q => {
    if (!q || typeof q !== 'object') return false;
    if (!allowedTypes.has(q.type)) return false;
    if (typeof q.title !== 'string' || !q.title.trim()) return false;

    // options only for MCQ/checkbox
    if (q.type === 'multipleChoice' || q.type === 'checkbox') {
      q.options = coerceStringArray(q.options).slice(0, 12);
      if (q.options.length === 0) return false;
    } else {
      delete q.options;
    }
    // keep helpText if string
    if (q.helpText && typeof q.helpText !== 'string') delete q.helpText;

    return true;
  });
}

// --- HARD GUARDRAIL: Force Preferred location options from events.venue_options ---
if (form && isFormObject(form)) {
  const canonicalVenueLabels = toVenueLabels($json.venue_options || []);
  if (canonicalVenueLabels.length > 0) {
    // Find the "Preferred location" question (case-insensitive)
    const locQ = form.questions.find(
      q => q && typeof q.title === 'string' && /preferred location/i.test(q.title)
    );
    if (locQ) {
      locQ.type = "multipleChoice";
      locQ.options = canonicalVenueLabels.slice(0, 12); // keep sane limit
      if (locQ.helpText && typeof locQ.helpText !== 'string') delete locQ.helpText;
    } else {
      // If missing, append it
      form.questions.splice(3, 0, {
        type: "multipleChoice",
        title: "Preferred location",
        options: canonicalVenueLabels.slice(0, 12)
      });
    }
  }
}

// 6) Pass through handy context
const passthrough = {
  event_id: $json.event_id ?? null,
  username: $json.username ?? null,
  event_name: $json.event_name ?? null
};

// 7) Emit normalized payload
return [{
  json: {
    questionnaire_needed: questionnaireNeeded,
    agent_response: agentResponse,
    form,                              // object or null
    confidence,
    debug_low_confidence_reasons: debugReasons,
    ...passthrough
  }
}];

