// The Department Agent returns something like:
// ```json\n{ "mentioned_department": "marketing", "is_valid": true, ... }\n```
// We'll strip formatting, parse it, and prepare for the SQL query.

let rawOutput = $json.output || "";

try {
  // Remove ```json and ``` markers, plus any escaped newlines/spaces
  rawOutput = rawOutput
    .replace(/```json|```/g, "")
    .replace(/\\n/g, "")
    .replace(/\s+/g, " ")
    .trim();
} catch (e) {
  rawOutput = $json.output;
}

let parsed = {};
try {
  parsed = JSON.parse(rawOutput);
} catch (e) {
  parsed = {
    mentioned_department: null,
    is_valid: false,
    need_prompt: true,
    response: "Sorry, I couldn’t interpret the department. Could you please specify?",
  };
}

// Normalize department into an array for SQL query
const departmentArray = parsed.mentioned_department
  ? [parsed.mentioned_department.toLowerCase()]
  : [];

return [
  {
    json: {
      departments: departmentArray,
      mentioned_department: parsed.mentioned_department,
      is_valid: parsed.is_valid ?? false,
      need_prompt: parsed.need_prompt ?? true,
      response: parsed.response ?? "",
    },
  },
];

// Clean and parse the Invite Agent output safely
let raw = $json.output || $json; // handle both direct and nested output
if (typeof raw !== 'string') raw = JSON.stringify(raw);

// Remove Markdown-style code fences and trim whitespace
const cleaned = raw
  .replace(/```json/i, '')
  .replace(/```/g, '')
  .trim();

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (e) {
  throw new Error("Invite Agent output is not valid JSON:\n" + cleaned);
}

return [{ json: parsed }];
