// --- Final Format Answer Node (chat-only output) ---
// Keeps internal structure but only passes forward the plain text for display.

let raw = $json.output || $json;
let out;

// Parse safely
try {
  out = typeof raw === "string" ? JSON.parse(raw) : raw;
} catch (err) {
  out = raw;
}

// Extract key pieces
const answer = out?.answer || "No clear answer found.";
const confidence = typeof out?.confidence === "number" ? out.confidence : 0.5;
const followUps = out?.follow_ups || [];
const sources = (out?.citations || []).map(c => c.label || c.type).join(", ");
const isLow = confidence < 0.6;

// Build readable chat message (no confidence or JSON metadata)
let text = "";

if (!isLow) {
  text += `${answer}\n\n`;
  if (sources) text += `Sources: ${sources}`;
} else {
  text += `Tentative answer: ${answer}\n\n`;
  if (followUps.length > 0) {
    text += `Can you clarify: ${followUps.join(" · ")}?`;
  } else {
    text += `Can you clarify a few details to improve my answer?`;
  }
}

// ✨ Return ONLY the text output
return [
  {
    json: {
      text_output: text.trim()
    }
  }
];

// Collect likely fields where the model might put the JSON
const candidates = [
  $json.schema,
  $json.output,               // <-- your case
  $json.kwargs?.content,
  $json.content,
  $json.text,
  $json.message,
  $json.response,
];

// Pick the first non-null/undefined candidate
let raw = candidates.find(v => v !== undefined && v !== null) ?? '';

function safeParse(input) {
  if (input && typeof input === 'object') return input; // already an object
  const s = String(input);
  // If the model wrapped the JSON with any extra text, extract the first {...} block
  const start = s.indexOf('{');
  const end   = s.lastIndexOf('}');
  const core  = (start >= 0 && end >= start) ? s.slice(start, end + 1) : s;

  try {
    return JSON.parse(core);
  } catch {
    // Fallback schema (so Apps Script still creates a form)
    return {
      title: "Untitled Event",
      description: "Auto-generated placeholder (invalid JSON).",
      questions: [
        { type: "shortAnswer",    title: "Full Name" },
        { type: "multipleChoice", title: "Can you attend?", options: ["Yes","No"] },
        { type: "paragraph",      title: "Dietary restrictions or comments" }
      ]
    };
  }
}

// n8n expects an array of items
return [{ json: safeParse(raw) }];
